import { useState } from "react";
import { cardapio } from "./arrayPizzasInicial";

export default function App() {
  const [listaCardapio, setListaCardapio] = useState(cardapio);
  return (
    <div>
      {/* <Header /> */}
      {/* <ButtonAddPizza /> */}
      <ItemsList listaCardapio={listaCardapio} />
      {/* <ModalPizza/> */}
      {/* <Cart /> */}
      {/* <Footer /> */}
    </div>
  );
}

function ItemsList({ listaCardapio }) {
  return (
    <ul>
      {listaCardapio.map((item, i) => (
        <li>
          {" "}
          <Item item={item} />{" "}
        </li>
      ))}
    </ul>
  );
}

function Item({ item }) {
  return (
    <li>
      <img src={`pizza${i + 1}`} />
      <h3>{item.name}</h3>
      <p>{item.description}</p>
      <p>{item.price}</p>
    </li>
  );
}
