import { useState } from "react";
import { cardapio } from "./arrayPizzasInicial";
import "./index.css";

export default function App() {
  const [listaCardapio, setListaCardapio] = useState(cardapio);
  return (
    <div>
      {/* <Header /> */}
      {/* <ButtonAddPizza /> */}
      <ItemsList listaCardapio={listaCardapio} />
      {/* <ModalPizza/> */}
      {/* <Cart /> */}
      {/* <Footer /> */}
    </div>
  );
}

function ItemsList({ listaCardapio }) {
  return (
    <ul className="pizza-area">
      {listaCardapio.map((item) => (
        <Item item={item} key={item.id} />
      ))}
    </ul>
  );
}

function Item({ item }) {
  const price =
    item.prices && item.prices[2] !== undefined
      ? item.prices[2]
      : "Preço não disponível";

  return (
    <li className="pizzas">
      <img src={item.image} alt={item.name} />
      <h3>{item.name}</h3>
      <p>{item.description}</p>
      <p>{price}</p> {/* Exibe um preço ou uma mensagem caso esteja ausente */}
      <button>+</button>
    </li>
  );
}
